function initAddClassOnPageLoad(){
	var overlay = jQuery('.js-overlay');
	
	overlay.animate({
		opacity: 0
	}, 500, function(){
		overlay.hide();
		jQuery('.body').addClass('page-loaded');
	});
}

function initCastomScrollCarousel(){
	jQuery('.slider').each(function(){
		var holder = jQuery(this);
		var mask = holder.find('.mask');
		var slides = mask.find('.slide');
		var btnPrev = holder.find('.btn-prev');
		var btnNext = holder.find('.btn-next');
		var maskWidth;
		var slidesWidth;
		var timer;
		var animateFlag = false;
		var autoRotationFlag = true;
		var rightHolderPosition;
		var rightSlidePosition;
		var leftSlidePosition;
		var leftHolderPosition;

		function resizeHandler(){
			maskWidth = mask.outerWidth(true);
			slidesWidth = slides.eq(0).outerWidth(true)*slides.length;
			rightHolderPosition = holder.offset().left + holder.width();
			rightSlidePosition = slides.eq(slides.length - 1).offset().left + slides.eq(slides.length - 1).outerWidth();
			leftSlidePosition = holder.offset().left;
			leftHolderPosition = slides.eq(0).offset().left;
		}

		function nextSlide(){
			if(!animateFlag){
				animateFlag = true;
				var left = Math.min(slidesWidth, mask.scrollLeft() + maskWidth);
				rightHolderPosition = holder.offset().left + holder.width();
				rightSlidePosition = slides.eq(slides.length - 1).offset().left + slides.eq(slides.length - 1).outerWidth();

				if(rightHolderPosition >= rightSlidePosition){
					left = 0
				}

				ScrollLeftTo(left);
			}
		}

		function prevSlide(){
			if(!animateFlag){
				animateFlag = true;
				var left = Math.max(0, mask.scrollLeft() - maskWidth);
				leftSlidePosition = holder.offset().left;
				leftHolderPosition = slides.eq(0).offset().left;

				if(leftSlidePosition === leftHolderPosition){
					left = slides.eq(0).outerWidth(true)*slides.length - holder.outerWidth(true);
				}

				ScrollLeftTo(left);
			}
		}

		function autorotation(){
			if(autoRotationFlag){
				timer = setTimeout(function(){
					nextSlide();
				},3000);
			}
		}

		function docEvent(e){
			var event = getEvent(e);
			var target = jQuery(e.target);

			if(target.closest(holder).length){
				if(autoRotationFlag){
					clearTimeout(timer);
					autoRotationFlag = false;
				}
			}else{
				if(!autoRotationFlag){
					autoRotationFlag = true;
					autorotation();
				}
			}
		}
		
		function getEvent(e) {
			if(e.originalEvent.changedTouches) {
				return e.originalEvent.changedTouches[0];
			} else if(typeof e.originalEvent.pageX === 'number') {
				return e.originalEvent;
			} else {
				return e;
			}
		}
			
		jQuery(document).on('mouseover click touchstart', docEvent);
		autorotation();
		resizeHandler();
		jQuery(window).on('load resize orientationchange', resizeHandler);
		btnPrev.on('click', function(e){
			e.preventDefault();
			prevSlide();
		});
		btnNext.on('click', function(e){
			e.preventDefault();
			nextSlide();
		});

		ScrollLeftTo = function(left, speed){
			var isWinPhoneDevice = navigator.msPointerEnabled && /MSIE 10.*Touch/.test(navigator.userAgent);
			if(isWinPhoneDevice){
				window.scrollLeft(left);
				animateFlag = false;
				return;
			}
			mask.animate({
				scrollLeft: left
			}, {
				duration: speed || 500,
				complete: function(){
					animateFlag = false;
					if(autoRotationFlag){
						autorotation();
					}
				}
			});
		}
	});
}

function initPositionFixed(){
	var fixedClass = 'mobile-fixed';
	var win = jQuery(window);
	var body = jQuery('body');
	jQuery('.sticky-box').each(function(){
		var box = jQuery(this);
		var scrollTop;
		var topOffset;
		var boxHeight;
		var positionFlag = true;
		var fakeBox = jQuery('<div class="fake-sticky" />').css({
			width: '100%',
			overflow: 'hidden'
		});
		fakeBox.insertBefore(box);
		
		function resizeHandler(){
			boxHeight = box.outerHeight(true);
			topOffset = fakeBox.offset().top;
			fakeBox.css({
				height: boxHeight
			});

			scrollHandler();
		}

		resizeHandler();
		
		function scrollHandler(){
			scrollTop = win.scrollTop();
			topOffset = fakeBox.offset().top;

			if(scrollTop > topOffset){
				if(positionFlag){
					positionFlag = false;
					activeSticker();
				}
			} else {
				if(!positionFlag){
					positionFlag = true;
					deactiveSticker();
				}
			}
		}
		
		function activeSticker(){
			body.addClass(fixedClass);
		}
		
		function deactiveSticker(){
			body.removeClass(fixedClass);
		}

		win.on('load resize orientationchange', resizeHandler).on('scroll', scrollHandler);
	});
}

function initHideNav(){
	var hiddenClass = 'nav-hidden';
	var win = jQuery(window);
	var body = jQuery('body');
	var footer = jQuery('#footer');
	var isiPad = navigator.userAgent.match(/iPad/i) != null;
	
	jQuery('.left-nav').each(function(){
		var box = jQuery(this);
		var scrollTop;
		var topOffset;
		var winHeight;
		var positionFlag = true;
		var footerHeight;
		var extraHeight = isiPad ? 60 : 0;
		console.log(extraHeight)
		
		function resizeHandler(){
			topOffset = footer.offset().top;
			footerHeight = footer.innerHeight();
			winHeight = win.height();
			
			scrollHandler();
		}

		resizeHandler();
		
		function scrollHandler(){
			scrollTop = win.scrollTop();
			if(scrollTop + win.height() + extraHeight >= footer.offset().top + footer.innerHeight()){
				if(positionFlag){
					positionFlag = false;
					activeSticker();
				}
			} else {
				if(!positionFlag){
					positionFlag = true;
					deactiveSticker();
				}
			}
		}
		
		function activeSticker(){
			body.addClass(hiddenClass);
		}
		
		function deactiveSticker(){
			body.removeClass(hiddenClass);
		}

		win.on('load resize orientationchange', resizeHandler).on('scroll', scrollHandler);
	});
}

function initIsotopeFilter(){
	var activeClass = 'active';
	jQuery('.isotope-filter').each(function() {
		var holder = jQuery(this),
			filterLinks = holder.find('.filters ul a'),
			btnShowAll = holder.find('.show-all'),
			container = holder.find('.sort-boxes-holder'),
			sortBlock = holder.find('.sort-boxes'),
			items = container.children();

		// initialize isotope
		container.isotope({
			layoutMode: 'packery',
			itemSelector : '.sort-box'
		});

		function initialFilter(){
			var activeLink = filterLinks.closest('li').filter('.' + activeClass).find('a');
			applyFilter(activeLink);
		}

		function applyFilter(link) {
			var selectedFilter = link.attr('rel');
			var filterRel = '[rel="'+selectedFilter+'"]';
			sortBlock.css({
				height: sortBlock.height()
			});
			setTimeout(function() {
				sortBlock.css({
					height: ''
				})
			}, 50);
			if (link.is(btnShowAll)) filterRel = '[rel]';
			container.isotope({filter : filterRel});
			refreshActiveClass(link);
		}

		// disable animation for IE<9
		if (!jQuery.support.opacity) {
			container.isotope({
				animationOptions: {
					duration: 0
				}
			});
		}

		initialFilter();

		// handle filter click
		filterLinks.on('touchstart click', function(e) {
			e.preventDefault();
			applyFilter(jQuery(this));
		});

		// handle show all click
		btnShowAll.on('touchstart click', function(e)  {
			e.preventDefault();
			applyFilter(btnShowAll);
		});

		// active class
		function refreshActiveClass(link) {
			filterLinks.parent().removeClass(activeClass);
			link.parent().addClass(activeClass);
		}
	});
}


function initCustomCarousel(){
	var win = jQuery(window);
	var gallery = jQuery('.js-slideset');
	var getWindowHeight = function () {
		return window.innerHeight || document.documentElement.clientHeight;
	};
	var onResize = function() {
		var winWidth = win.width();
		var winHeight = getWindowHeight();
		
		gallery.find('.image-area').css({
			height: '',
			width: ''
		}).each(function() {
			var area = jQuery(this);
			var height = area.height();
			var width = area.width();
			
			if (width > winWidth) {
				area.css('width', winWidth);
			}
			if (height > winHeight) {
				area.css('height', winHeight);
			}
		});
	};
	onResize();
	var galleryAPI = gallery.slick({
		speed: 500,
		autoplay: false,
		autoplaySpeed: 2000,
		slidesToShow: 1,
		pauseOnHover: false,
		centerMode: true,
		variableWidth: true
		// responsive: [
			// {
				// breakpoint: 901,
				// settings: {
					// slidesToShow: 1
				// }
			// },
			// {
				// breakpoint: 768,
				// settings: {
					// slidesToShow: 1
				// }
			// }
		// ]
	});
	
	function getEvent(e) {
		if(e.originalEvent.changedTouches) {
			return e.originalEvent.changedTouches[0];
		} else if(typeof e.originalEvent.pageX === 'number') {
			return e.originalEvent;
		} else {
			return e;
		}
	}
	
	function docEvent(e){
		var event = getEvent(e);
		var eventX = event.pageX;
		var target = jQuery(e.target);
		var extraSpace = 100;

		if(target.closest(gallery).length){
			if(eventX > gallery.offset().left + gallery.innerWidth()/3 && eventX < gallery.offset().left + 2*(gallery.innerWidth()/3)){
				galleryAPI.slickPause();
			}else if(eventX > gallery.offset().left + extraSpace && eventX < gallery.offset().left + gallery.innerWidth() - extraSpace){
				gallery.slickSetOption('autoplaySpeed', 2000, false);
				galleryAPI.slickPlay();
			}else{
				gallery.slickSetOption('autoplaySpeed', 500, false);
				galleryAPI.slickPlay();
			}
		}else{
			gallery.slickSetOption('autoplaySpeed', 2000, false);
			galleryAPI.slickPlay();
		}
	}
	
	onResize();
	win.on('resize', onResize);
	jQuery(document).on('mouseover click touchstart', docEvent);
}

